import { LitElement, css, html } from 'lit'
import { customElement, property } from 'lit/decorators.js'
import { convertToInlineStyles, litName } from '../../utils/index'

const ImgPlaceholder =
  'data:image/png;base64,'
@customElement(litName('img'))
export default class Image extends LitElement {
  static styles = [
    css`
      :host {
        overflow: hidden;
        display: block;
        position: relative;
      }
    `,
    css`
      .fin-image {
        width: 100%;
        height: 100%;
        display: block;
        position: relative;
      }
      .img-placeholder {
        position: absolute;
        background-color: #f5f5f5;
        height: 100%;
        width: 100%;
        left: 0;
        top: 0;
        min-height: 24px;
        min-width: 24px;
        z-index: 1;
      }
    `,
  ]
  @property({ type: String, reflect: true }) src = ''
  @property({ type: String }) width = ''
  @property({ type: String }) height = ''
  @property({ type: String }) alt = ''
  @property({ type: String }) fit = 'fill'
  @property({ type: String }) 'border-radius' = ''
  private _showPlaceholder: boolean = true
  constructor() {
    super()
  }
  willUpdate(changedProperties: Map<PropertyKey, unknown>) {
    if (changedProperties.has('src')) {
      this._showPlaceholder = true
    }
  }
  render() {
    const style = {
      width: this.width,
      height: this.height,
      'border-radius': this['border-radius'],
    }
    return html`
      <style>
        :host {${convertToInlineStyles(style)}}
      </style>
      ${this._showPlaceholder
        ? html`<img class="img-placeholder" src=${ImgPlaceholder} alt="" />`
        : ''}
      <img
        .alt=${this.alt}
        class="fin-image"
        src=${this.src}
        style="object-fit: ${this.fit}"
        @load=${this.loadSuccess}
        @error=${this.loadError}
      />
    `
  }
  loadSuccess(e: Event) {
    this._showPlaceholder = false
    this.requestUpdate()
    this.dispatchEvent(new CustomEvent('load', { detail: e }))
  }
  loadError(e: Event) {
    this.dispatchEvent(new CustomEvent('error', { detail: e }))
  }
}
